youtube.loadJs:
function deshow(txt){
    txt=txt.replace(/\x22/g,'"');
    txt=txt.replace(/\x3d/g,'=');
    txt=txt.replace(/\x7b/g,'{');
    txt=txt.replace(/\x7d/g,'}');
    txt=txt.replace(/\x5b/g,'[');
    txt=txt.replace(/\x5d/g,']');
    txt=txt.replace(/\x27/g,"'");
    return txt;
}

